        task_data = dict(id='0xDEADBEEF', content='{ }', name='Dead Beef', url=None)
        check_output.side_effect = does_what_its_told
        mkdtemp.side_effect = same_tempdir_every_time
        
        job_id, content = task_data['id'], task_data['content']
